   const [channel, setChannel] = useState('');
   const [threadBody, setBody] = useState('');
   const [threadAuthor, setAuthor] = useState('');
   const [replies, setReplies] = useState([]);
   const placeHolder = 'message #' + channel;

    function giveThreads(channelSelected){
        if (channelSelected === 'A'){
            return <div>Hello</div>
        } else if (channelSelected === 'B'){
            return <div>World</div>
        } else if (channelSelected === 'C') {
            return <div>Child</div>
        }
    }

   return (
    <div className="main">
        <table>
            <tr>
                <td className="aside">
                <Aside click={setChannel}/>
                </td>
                <td className="feed">
                    <div>{giveThreads(channel)}</div> 
                    {/* <div className="reply">
                        <textarea 
                        className="textArea" 
                        placeholder={placeHolder}
                        rows="4" 
                        cols="50"
                        />
                    </div> */}
                </td>
            </tr>
        </table>

    </div>
    );
}
