import React, { useState } from 'react';
import Aside from './aside';
import Reply from './reply';
import Thread from './thread';
import '../css/mainView.css';
import threads from '../data/threads';


export default function Main() {
   const [channel, setChannel] = useState('');
   const [repliesList, setRepliesList] = useState([]);
   const [showThreads, setShowThreads] = useState(true);
   const [showReplies, setShowReplies] = useState(false);
   const placeHolder = 'message #' + channel;

    function giveThreads(channelSelected){
        const threadList = Object.values(threads);
        const threadRenderList = []
        for (let i = 0; i < threadList.length; i += 1){
            if (threadList[i].channel === channelSelected){
                threadRenderList.push(<Thread title={threadList[i].title} replies={threadList[i].messages} by={threadList[i].creator} click1={setRepliesList} click2={setShowReplies}/>)

            } 
        }
        return threadRenderList;
    }
    function renderReplies(mainMessage, messageList) {
        let renderRepliesList = [<div>{mainMessage}</div>]
        let temp = '';
        for (let i = 0; i < messageList.length; i += 1 ){
            temp = mainMessage;
            renderRepliesList.push(<div>{messageList[i]}</div>)
        }
        return renderRepliesList;    
    }

   return (
    <div className="main">
        <table>
            <tr>
                <td className="aside">
                <Aside click={setChannel}/>
                </td>
                <td className="feed">
                    {!showReplies && <div>{giveThreads(channel)}</div> }
                    {showReplies && <div>{renderReplies("hello", repliesList)}</div> }
                    <Reply holder={placeHolder} />
                </td>
            </tr>
        </table>

    </div>
    );
}
