import React, { useState } from 'react';
import Aside from './aside';
import Reply from './reply';
import Thread from './thread';
import '../css/mainView.css';
import threads from '../data/threads';


export default function Main() {
   const [channel, setChannel] = useState('');
   const [threadBody, setBody] = useState('');
   const [threadAuthor, setAuthor] = useState('');
   const [replies, setReplies] = useState([]);
   const placeHolder = 'message #' + channel;

    function giveThreads(channelSelected){
        const threadList = Object.values(threads);
        const threadRenderList = []
        for (let i = 0; i < threadList.length; i += 1){
            if (threadList[i].channel === channelSelected){
                threadRenderList.push(<div>{threadList[i].title}</div>)

            } 
        }
        return threadRenderList;
    }

   return (
    <div className="main">
        <table>
            <tr>
                <td className="aside">
                <Aside click={setChannel}/>
                </td>
                <td className="feed">
                    <div>{giveThreads(channel)}</div> 
                    <Reply holder={placeHolder} />
                </td>
            </tr>
        </table>

    </div>
    );
}
